-- ----------------------------
-- Table structure for t_message
-- ----------------------------
DROP TABLE IF EXISTS `t_message`;
CREATE TABLE `t_message` (
  `msg_id` int(11) NOT NULL AUTO_INCREMENT,
  `msg_pid` int(11) NOT NULL DEFAULT '0',
  `send__role` int(11) NOT NULL,
  `student_id` int(11) NOT NULL,
  `user_id` int(11) NOT NULL,
  `msg_content` varchar(255) NOT NULL,
  `msg_add_time` datetime DEFAULT NULL,
  `msg_is_reply` int(11) DEFAULT '0',
  `msg_is_read` int(11) DEFAULT '0',
  PRIMARY KEY (`msg_id`),
  KEY `FK_MESSAGE_ROLE` (`send__role`),
  KEY `FK_MESSAGE_USER` (`user_id`),
  KEY `FK_MESSAGE_STUDENGT` (`student_id`),
  CONSTRAINT `FK_MESSAGE_ROLE` FOREIGN KEY (`send__role`) REFERENCES `t_role` (`role_id`),
  CONSTRAINT `FK_MESSAGE_STUDENGT` FOREIGN KEY (`student_id`) REFERENCES `t_student` (`s_id`),
  CONSTRAINT `FK_MESSAGE_USER` FOREIGN KEY (`user_id`) REFERENCES `t_user` (`user_id`)
) ENGINE=InnoDB AUTO_INCREMENT=45 DEFAULT CHARSET=utf8;

