INSERT INTO `t_notice` VALUES ('34', '关于游毅等同志任职的通知', '<p class=\"MsoNormal\" style=\"margin-top:0px;margin-bottom:0px;padding:0px;color:#333333;font-family:\'Lucida Grande\', Verdana, Lucida, Arial, Helvetica, \'Times New Roman\', 宋体, sans-serif;white-space:normal;line-height:28pt;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:16pt;font-family:仿宋_GB2312;\">各系、各部门：</span><span lang=\"EN-US\" style=\"font-size:16pt;\"><o:p></o:p></span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-top:0px;margin-bottom:0px;padding:0px;color:#333333;font-family:\'Lucida Grande\', Verdana, Lucida, Arial, Helvetica, \'Times New Roman\', 宋体, sans-serif;white-space:normal;line-height:28pt;text-indent:32.25pt;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:16pt;font-family:仿宋_GB2312;\">因机构调整，经院务会研究决定：<span lang=\"EN-US\"><o:p></o:p></span></span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-top:0px;margin-bottom:0px;padding:0px;color:#333333;font-family:\'Lucida Grande\', Verdana, Lucida, Arial, Helvetica, \'Times New Roman\', 宋体, sans-serif;white-space:normal;line-height:28pt;text-indent:32.25pt;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:16pt;font-family:仿宋_GB2312;\">任命游<span lang=\"EN-US\">&nbsp;&nbsp;</span>毅同志为教务处处长；<span lang=\"EN-US\"><o:p></o:p></span></span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-top:0px;margin-bottom:0px;padding:0px;color:#333333;font-family:\'Lucida Grande\', Verdana, Lucida, Arial, Helvetica, \'Times New Roman\', 宋体, sans-serif;white-space:normal;line-height:28pt;text-indent:32.25pt;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:16pt;font-family:仿宋_GB2312;\">任命姚东生同志为教务处副处长；<span lang=\"EN-US\"><o:p></o:p></span></span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-top:0px;margin-bottom:0px;padding:0px;color:#333333;font-family:\'Lucida Grande\', Verdana, Lucida, Arial, Helvetica, \'Times New Roman\', 宋体, sans-serif;white-space:normal;line-height:28pt;text-indent:32.25pt;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:16pt;font-family:仿宋_GB2312;\">任命罗<span lang=\"EN-US\">&nbsp;&nbsp;</span>翔同志为教务处教务办公室主任；<span lang=\"EN-US\"><o:p></o:p></span></span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-top:0px;margin-bottom:0px;padding:0px;color:#333333;font-family:\'Lucida Grande\', Verdana, Lucida, Arial, Helvetica, \'Times New Roman\', 宋体, sans-serif;white-space:normal;line-height:28pt;text-indent:32.25pt;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:16pt;font-family:仿宋_GB2312;\">任命胡<span lang=\"EN-US\">&nbsp;&nbsp;</span>颖同志为教务处评建办公室主任。<span lang=\"EN-US\"><o:p></o:p></span></span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-top:0px;margin-bottom:0px;padding:0px;color:#333333;font-family:\'Lucida Grande\', Verdana, Lucida, Arial, Helvetica, \'Times New Roman\', 宋体, sans-serif;white-space:normal;line-height:28pt;text-indent:32.25pt;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:16pt;font-family:仿宋_GB2312;\">以上任命同志原任职务自然免除，自<st1:chsdate w:st=\"on\" isrocdate=\"False\" islunardate=\"False\" day=\"1\" month=\"3\" year=\"2015\"><span lang=\"EN-US\">2015</span>年<span lang=\"EN-US\">3</span>月<span lang=\"EN-US\">1</span>日起</st1:chsdate>执行。<span lang=\"EN-US\"><o:p></o:p></span></span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-top:0px;margin-bottom:0px;padding:0px;color:#333333;font-family:\'Lucida Grande\', Verdana, Lucida, Arial, Helvetica, \'Times New Roman\', 宋体, sans-serif;line-height:15.6000003814697px;white-space:normal;background-color:#FFFFFF;\">\r\n	<span lang=\"EN-US\" style=\"font-size:16pt;\">&nbsp;</span>\r\n</p>\r\n<p class=\"MsoNormal\" align=\"right\" style=\"margin-top:0px;margin-bottom:0px;padding:0px;color:#333333;font-family:\'Lucida Grande\', Verdana, Lucida, Arial, Helvetica, \'Times New Roman\', 宋体, sans-serif;white-space:normal;line-height:28pt;text-indent:96pt;background-color:#FFFFFF;\">\r\n	<span lang=\"EN-US\" style=\"font-size:16pt;font-family:仿宋_GB2312;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-size:16pt;font-family:仿宋_GB2312;\">福州大学至诚学院<span lang=\"EN-US\"><o:p></o:p></span></span>\r\n</p>\r\n<p class=\"MsoNormal\" align=\"right\" style=\"margin-top:0px;margin-bottom:0px;padding:0px;color:#333333;font-family:\'Lucida Grande\', Verdana, Lucida, Arial, Helvetica, \'Times New Roman\', 宋体, sans-serif;white-space:normal;text-align:right;line-height:28pt;background-color:#FFFFFF;\">\r\n	<span lang=\"EN-US\" style=\"font-size:16pt;font-family:仿宋_GB2312;\">&nbsp;</span><span style=\"font-size:16pt;font-family:仿宋_GB2312;\">二Ｏ一五年三月一日</span>\r\n</p>', '1', '2015-04-07 21:21:49');
INSERT INTO `t_notice` VALUES ('35', '院领导接待日（2015年3月）安排表', '<p class=\"MsoNormal\" style=\"margin-top:0px;margin-bottom:0px;padding:0px;color:#333333;font-family:\'Lucida Grande\', Verdana, Lucida, Arial, Helvetica, \'Times New Roman\', 宋体, sans-serif;white-space:normal;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:12pt;font-family:宋体;line-height:24px;\">各系、各部门：<span lang=\"EN-US\"><o:p></o:p></span></span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-top:0px;margin-bottom:0px;padding:0px;color:#333333;font-family:\'Lucida Grande\', Verdana, Lucida, Arial, Helvetica, \'Times New Roman\', 宋体, sans-serif;white-space:normal;background-color:#FFFFFF;\">\r\n	<span lang=\"EN-US\" style=\"font-size:12pt;font-family:宋体;line-height:24px;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-size:12pt;font-family:宋体;line-height:24px;\">现将<span lang=\"EN-US\">2015</span>年<span lang=\"EN-US\">3</span>月份院领导接待日安排表公布如下：<span lang=\"EN-US\"><o:p></o:p></span></span>\r\n</p>\r\n<table class=\"MsoTableGrid\" cellspacing=\"0\" cellpadding=\"0\" width=\"603\" border=\"1\" style=\"color:#333333;font-family:\'Lucida Grande\', Verdana, Lucida, Arial, Helvetica, \'Times New Roman\', 宋体, sans-serif;font-size:12px;line-height:15.6000003814697px;border:medium none;width:451.95pt;background-color:#FFFFFF;\">\r\n	<tbody>\r\n		<tr>\r\n			<td valign=\"top\" width=\"189\" style=\"border:1pt solid windowtext;width:142pt;padding:0cm 5.4pt;\">\r\n				<p class=\"MsoNormal\" align=\"center\" style=\"margin-top:0px;margin-bottom:0px;padding:0px;text-align:center;\">\r\n					<span style=\"font-size:12pt;font-family:宋体;line-height:24px;\">时间<span lang=\"EN-US\"><o:p></o:p></span></span>\r\n				</p>\r\n			</td>\r\n			<td valign=\"top\" width=\"73\" style=\"border-top-color:windowtext;border-width:1pt 1pt 1pt medium;border-style:solid solid solid none;border-right-color:windowtext;width:54.8pt;border-bottom-color:windowtext;padding:0cm 5.4pt;\">\r\n				<p class=\"MsoNormal\" align=\"center\" st