-- ----------------------------
-- View structure for v_course
-- ----------------------------
DROP VIEW IF EXISTS `v_course`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER  VIEW `v_course` AS select `t1`.`c_id` AS `c_id`,`t1`.`c_name` AS `c_name`,`t1`.`t_id` AS `t_id`,`t1`.`c_attend_time` AS `c_attend_time`,`t1`.`c_attend_addr` AS `c_attend_addr`,`t2`.`nick_name` AS `nick_name`,concat(`t4`.`aa_name`,'-',`t3`.`aa_name`) AS `aa_name` from (((`t_course` `t1` left join `t_user` `t2` on((`t1`.`t_id` = `t2`.`user_id`))) left join `t_attend_addr` `t3` on((`t1`.`c_attend_addr` = `t3`.`aa_id`))) left join `t_attend_addr` `t4` on((`t3`.`aa_pid` = `t4`.`aa_id`))) ;

-- ----------------------------
-- View structure for v_course_info
-- ----------------------------
DROP VIEW IF EXISTS `v_course_info`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER  VIEW `v_course_info` AS select `t1`.`ci_id` AS `ci_id`,`t1`.`c_id` AS `c_id`,`t1`.`ct_id` AS `ct_id`,`t1`.`s_id` AS `s_id`,`t1`.`attendance_info` AS `attendance_info`,`t1`.`work_info` AS `work_info`,`t2`.`s_no` AS `s_no`,`t2`.`s_name` AS `s_name`,`t3`.`c_name` AS `c_name`,`t4`.`ct_time` AS `ct_time` from (((`t_course_info` `t1` left join `t_course` `t3` on((`t1`.`c_id` = `t3`.`c_id`))) left join `t_student` `t2` on((`t1`.`s_id` = `t2`.`s_id`))) left join `t_course_time` `t4` on((`t1`.`ct_id` = `t4`.`ct_id`))) ;

-- ----------------------------
-- View structure for v_course_student
-- ----------------------------
DROP VIEW IF EXISTS `v_course_student`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER  VIEW `v_course_student` AS select `t1`.`cs_id` AS `cs_id`,`t1`.`c_id` AS `c_id`,`t1`.`s_id` AS `s_id`,`t2`.`s_no` AS `s_no`,`t2`.`s_name` AS `s_name` from (`t_course_student` `t1` left join `t_student` `t2` on((`t1`.`s_id` = `t2`.`s_id`))) ;

-- ----------------------------
-- View structure for v_message
-- ----------------------------
DROP VIEW IF EXISTS `v_message`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER  VIEW `v_message` AS select `t1`.`msg_id` AS `msg_id`,`t1`.`msg_pid` AS `msg_pid`,`t1`.`user_id` AS `user_id`,`t1`.`student_id` AS `student_id`,`t1`.`send__role` AS `send__role`,`t1`.`msg_content` AS `msg_content`,`t1`.`msg_add_time` AS `msg_add_time`,`t1`.`msg_is_read` AS `msg_is_read`,`t4`.`role_name` AS `role_name`,`t1`.`msg_is_reply` AS `msg_is_reply`,`t2`.`nick_name` AS `teacher_name`,`t3`.`s_name` AS `student_name`,`t3`.`s_no` AS `s_no` from (((`t_message` `t1` left join `t_user` `t2` on((`t1`.`user_id` = `t2`.`user_id`))) left join `t_student` `t3` on((`t1`.`student_id` = `t3`.`s_id`))) left join `t_role` `t4` on((`t1`.`send__role` = `t4`.`role_id`))) ;

-- ----------------------------
-- View structure for v_news
-- ----------------------------
DROP VIEW IF EXISTS `v_news`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER  VIEW `v_news` AS select `t1`.`news_id` AS `news_id`,`t1`.`news_title` AS `news_title`,`t1`.`news_content` AS `news_content`,`t1`.`add_user` AS `add_user`,`t1`.`add_time` AS `add_time`,`t2`.`nick_name` AS `nick_name` from (`t_news` `t1` left join `t_user` `t2` on((`t1`.`add_user` = `t2`.`user_id`))) ;

-- ----------------------------
-- View structure for v_notice
-- ----------------------------
DROP VIEW IF EXISTS `v_notice`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER  VIEW `v_notice` AS select `t1`.`notice_id` AS `notice_id`,`t1`.`notice_title` AS `notice_title`,`t1`.`notice_content` AS `notice_content`,`t1`.`add_user` AS `add_user`,`t1`.`add_time` AS `add_time`,`t2`.`nick_name` AS `nick_name` from (`t_notice` `t1` left join `t_user` `t2` on((`t1`.`add_user` = `t2`.`user_id`))) ;

