DROP VIEW IF EXISTS `v_student`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER  VIEW `v_student` AS select `t1`.`s_id` AS `s_id`,`t1`.`s_no` AS `s_no`,`t1`.`s_name` AS `s_name`,`t1`.`s_password` AS `s_password`,`t1`.`salt` AS `salt`,`t1`.`user_role` AS `user_role`,`t1`.`s_sex` AS `s_sex`,`t1`.`d_id` AS `d_id`,`t1`.`grade` AS `grade`,`t1`.`s_class` AS `s_class`,`t2`.`d_name` AS `d_name`,`t2`.`d_pid` AS `d_pid` from (`t_student` `t1` left join `t_department` `t2` on((`t1`.`d_id` = `t2`.`d_id`))) ;

