INSERT INTO `t_notice` VALUES ('28', '关于调整福州大学至诚学院教师等专业技术职务聘任委员会成员的通知', '<p class=\"MsoNormal\" align=\"left\" style=\"margin-top:0px;margin-bottom:0px;padding:0px;color:#333333;font-family:\'Lucida Grande\', Verdana, Lucida, Arial, Helvetica, \'Times New Roman\', 宋体, sans-serif;white-space:normal;background-color:#FFFFFF;\">\r\n	<span style=\"line-height:24px;font-family:宋体;color:black;font-size:12pt;\">各系、各部门：<span lang=\"EN-US\"><o:p></o:p></span></span>\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"margin-top:0px;margin-bottom:0px;padding:0px;color:#333333;font-family:\'Lucida Grande\', Verdana, Lucida, Arial, Helvetica, \'Times New Roman\', 宋体, sans-serif;white-space:normal;text-indent:24pt;background-color:#FFFFFF;\">\r\n	<span style=\"line-height:24px;font-family:宋体;color:black;font-size:12pt;\">鉴于人事变动，经研究，对我院专业技术职务聘任委员会成员作部分调整，调整后的成员名单如下（按姓氏笔画排序）：<span lang=\"EN-US\"><o:p></o:p></span></span>\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"margin-top:0px;margin-bottom:0px;padding:0px;color:#333333;font-family:\'Lucida Grande\', Verdana, Lucida, Arial, Helvetica, \'Times New Roman\', 宋体, sans-serif;white-space:normal;text-indent:24pt;background-color:#FFFFFF;\">\r\n	<span style=\"line-height:24px;font-family:宋体;color:black;font-size:12pt;\">主<span lang=\"EN-US\">&nbsp;&nbsp;</span>任：朱祖平<span lang=\"EN-US\"><o:p></o:p></span></span>\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"margin-top:0px;margin-bottom:0px;padding:0px;color:#333333;font-family:\'Lucida Grande\', Verdana, Lucida, Arial, Helvetica, \'Times New Roman\', 宋体, sans-serif;white-space:normal;text-indent:24pt;background-color:#FFFFFF;\">\r\n	<span style=\"line-height:24px;font-family:宋体;color:black;font-size:12pt;\">副主任：朱敏琛、胡继荣</span>\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"margin-top:0px;margin-bottom:0px;padding:0px;color:#333333;font-family:\'Lucida Grande\', Verdana, Lucida, Arial, Helvetica, \'Times New Roman\', 宋体, sans-serif;white-space:normal;