INSERT INTO `t_notice` VALUES ('34', '关于游毅等同志任职的通知', '<p class=\"MsoNormal\" style=\"margin-top:0px;margin-bottom:0px;padding:0px;color:#333333;font-family:\'Lucida Grande\', Verdana, Lucida, Arial, Helvetica, \'Times New Roman\', 宋体, sans-serif;white-space:normal;line-height:28pt;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:16pt;font-family:仿宋_GB2312;\">各系、各部门：</span><span lang=\"EN-US\" style=\"font-size:16pt;\"><o:p></o:p></span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-top:0px;margin-bottom:0px;padding:0px;color:#333333;font-family:\'Lucida Grande\', Verdana, Lucida, Arial, Helvetica, \'Times New Roman\', 宋体, sans-serif;white-space:normal;line-height:28pt;text-indent:32.25pt;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:16pt;font-family:仿宋_GB2312;\">因机构调整，经院务会研究决定：<span lang=\"EN-US\"><o:p></o:p></span></span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-top:0px;margin-bottom:0px;padding:0px;color:#333333;font-family:\'Lucida Grande\', Verdana, Lucida, Arial, Helvetica, \'Times New Roman\', 宋体, sans-serif;white-space:normal;line-height:28pt;text-indent:32.25pt;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:16pt;font-family:仿宋_GB2312;\">任命游<span lang=\"EN-US\">&nbsp;&nbsp;</span>毅同志为教务处处长；<span lang=\"EN-US\"><o:p></o:p></span></span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-top:0px;margin-bottom:0px;padding:0px;color:#333333;font-family:\'Lucida Grande\', Verdana, Lucida, Arial, Helvetica, \'Times New Roman\', 宋体, sans-serif;white-space:normal;line-height:28pt;text-indent:32.25pt;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:16pt;font-family:仿宋_GB2312;\">任命姚东生同志为教务处副处长；<span lang=\"EN-US\"><o:p></o:p></span></span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-top:0px;margin-bottom:0px;padding:0px;color:#333333;font-family:\'Lucida Grande\', Verdana, Lucida, Arial, Helvetica, \'Times New Roman\', 宋体, sans-serif;white-space:normal;line-height:28pt;text-indent:32.25pt;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:16pt;font-family:仿宋_GB2312;\">任命罗<span lang=\"EN-US\">&nbsp;&nbsp;</span>翔同志为教务处教务办公室主任；<span lang=\"EN-US\"><o:p></o:p></span></span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-top:0px;margin-bottom:0px;padding:0px;color:#333333;font-family:\'Lucida Grande\', Verdana, Lucida, Arial, Helvetica, \'Times New Roman\', 宋体, sans-serif;white-space:normal;line-height:28pt;text-indent:32.25pt;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:16pt;font-family:仿宋_GB2312;\">任命胡<span lang=\"EN-US\">&nbsp;&nbsp;</span>颖同志为教务处评建办公室主任。<span lang=\"EN-US\"><o:p></o:p></span></span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-top:0px;margin-bottom:0px;padding:0px;color:#333333;font-family:\'Lucida Grande\', Verdana, Lucida, Arial, Helvetica, \'Times New Roman\', 宋体, sans-serif;white-space:normal;line-height:28pt;text-indent:32.25pt;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:16pt;font-family:仿宋_GB2312;\">以上任命同志原任职务自然免除，自<st1:chsdate w:st=\"on\" isrocdate=\"False\" islunardate=\"False\" day=\"1\" month=\"3\" year=\"2015\"><span lang=\"EN-US\">2015</span>年<span lang=\"EN-US\">3</span>月<span lang=\"EN-US\">1</span>日起</st1:chsdate>执行。<span lang=\"EN-US\"><o:p></o:p></span></span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-top:0px;margin-bottom:0px;padding:0px;color:#333333;font-family:\'Lucida Grande\', Verdana, Lucida, Arial, Helvetica, \'Times New Roman\', 宋体, sans-serif;line-height:15.6000003814697px;white-space:normal;background-color:#FFFFFF;\">\r\n	<span lang=\"EN-US\" style=\"font-size:16pt;\">&nbsp;</span>\r\n</p>\r\n<p class=\"MsoNormal\" align=\"right\" style=\"margin-top:0px;margin-bottom:0px;padding:0px;color:#333333;font-family:\'Lucida Grande\', Verdana, Lucida, Arial, Helvetica, \'Times New Roman\', 宋体, sans-serif;white-space:normal;line-height:28pt;text-indent:96pt;background-color:#FFFFFF;\">\r\n	<span lang=\"EN-US\" style=\"font-size:16pt;font-family:仿宋_GB2312;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&