mapper.select:
SELECT v1.s_id,v1.s_no,v1.s_name, group_concat(`v1`.`attendance_info` separator ',') AS `attendances`, group_concat(`v1`.`attendance` separator ',') AS attendance_infos from (select `t1`.`s_id`,t2.s_no,t2.s_name,`t1`.`attendance_info`,count(`t1`.`attendance_info`) AS `attendance` from `t_course_info` `t1` LEFT JOIN t_student t2 ON t1.s_id=t2.s_id where t1.c_id=#{cId}
and t1.s_id=#{sId}
group by `t1`.`s_id`,`t1`.`attendance_info`) v1 group by `v1`.`s_id`

SELECT v1.s_id,v1.s_no,v1.s_name, group_concat(`v1`.`work_info` separator ',') AS `works`, group_concat(`v1`.`work` separator ',') AS work_infos from (select `t1`.`s_id`,t2.s_no,t2.s_name,`t1`.`work_info`,count(`t1`.`work_info`) AS `work` from `t_course_info` `t1` LEFT JOIN t_student t2 ON t1.s_id=t2.s_id where t1.c_id=#{cId}
and t1.s_id=#{sId}
group by `t1`.`s_id`,`t1`.`work_info`) v1 group by `v1`.`s_id`

SELECT t3.s_id as sId,t3.s_no as sNo,t3.s_name as sName
from t_work t1
LEFT JOIN t_course_student t2 ON t1.c_id=t2.c_id
LEFT JOIN t_student t3 ON t2.s_id=t3.s_id
WHERE t1.w_id=#{wId}
LIMIT #{page.start},#{page.limit}
